import React from 'react';

import { Layout, Menu, Breadcrumb } from 'antd';
import { Link, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import * as actions from '../store/actions/auth';


const { Header, Content, Footer } = Layout;


class CustomLayout extends React.Component{

  render(){
    return(
      <Layout className="layout">
        <Header>

          <Menu
            theme="dark"
            mode="horizontal"
            defaultSelectedKeys={['1']}
            style={{ lineHeight: '64px' }}
          >

            <Menu.Item key="1"><Link to="/login">BusinessDock</Link></Menu.Item>



            {

              this.props.isAuthenticated ?

              <div>
                <Menu.Item key="2">Outgoing</Menu.Item>
                <Menu.Item key="3">Incoming</Menu.Item>
                <Menu.Item key="4">Employees</Menu.Item>
                <Menu.Item key="5">Inventory</Menu.Item>
                <Menu.Item key="6">Vendors</Menu.Item>
                <Menu.Item key="7">Reports</Menu.Item>
                <Menu.Item key="8" onClick={ this.props.logout} >
                  Logout
                </Menu.Item>
              </div>

              :

              <Menu.Item key="8">
                <Link to="/login/">Login</Link>
              </Menu.Item>



          }


          </Menu>
        </Header>


        <Content style={{ padding: '0 50px' }}>
          <Breadcrumb style={{ margin: '16px 0' }}>
            <Breadcrumb.Item>Home</Breadcrumb.Item>
            <Breadcrumb.Item>List</Breadcrumb.Item>
            <Breadcrumb.Item>App</Breadcrumb.Item>
          </Breadcrumb>
          <div style={{ background: '#fff', padding: 24, minHeight: 280 }}>
          { this.props.children }

          </div>
        </Content>
        <Footer style={{ textAlign: 'center' }}>
          BusinessDock Â©2018 Created by Mateo Sixtos
        </Footer>
      </Layout>

    );

  }
}


const mapDispatchToProps = dispatch =>{
  return {
    logout: () => dispatch(actions.logout())
  }
}

export default withRouter(connect(null,  mapDispatchToProps)(CustomLayout));
